import React, { useState, useEffect } from 'react';
import banner from '../assets/banner.jpg';
import './home.css';

function Home() {
    const images = [banner];
    const [current, setCurrent] = useState(0);

    useEffect(() => {
        const interval = setInterval(() => {
            setCurrent(prev => (prev + 1) % images.length);
        }, 3000);
        return () => clearInterval(interval);
    }, []);

    return (
        <div style={{margin: 0, padding: 0}}>
            <div 
                className="slider-container"
                style={{
                    margin: 0,
                    marginTop: 0,
                    padding: 0,
                    lineHeight: 0,
                    fontSize: 0
                }}
            >
                <img 
                    src={images[current]} 
                    alt="Banner" 
                    className="slider-image"
                    style={{
                        display: 'block',
                        margin: 0,
                        padding: 0,
                        verticalAlign: 'top',
                        lineHeight: 0
                    }}
                />
            </div>

            <div className="about-section">
                <h2>Welcome to Munoth Industries</h2>
                <p>
                    Our company came into existence in the year 2008 as a Private Limited venture with its operational
                    unit at Chennai, Tamil Nadu (India). We have our expertise in the domain of manufacturer and supplier
                    of various types of tissues. Our brief product catalog comprehends Kitchen Paper Napkin, Disposable
                    Tissue Paper, Tissue Paper Roll, Roll Dispenser and many more. Our products are manufactured using
                    rich grade raw material sourced from highly allied vendors of the market. Our vendors are
                    technologically advanced and cross check the quality of each product before making it ready for final
                    dispatch.
                </p>
                <p>
                    Moreover, our company possesses a state-of-the-art infrastructure that is equipped with a team of
                    high caliber professionals that forms our strength. We transmit our customers with best logistics
                    solutions ensuring time constraint deliveries of our products. With all such practices, we have enabled
                    our self to...
                </p>
            </div>
        </div>
    );
}

export default Home;
